import React, { useContext, useEffect, useState } from "react";
import { useLocation, useNavigate, useNavigation } from "react-router-dom";
import { AppContext } from "../context/AppContext";
import { baseUrl } from "../baseUrl";
import Header from "../components/Header";
import BlogDetails from "../components/BlogDetails";
import Pagination from "../components/Pagination";

const  BlogPage=() => {
    const newBaseUrl=" https://codehelp-apis.vercel.app/api/"
    const [blog, setBlog] = useState(null);
    const [relatedBlog, setRelatedBlog]=useState([]);
    const location = useLocation();
    const navigation = useNavigate();
    const {setLoading,loading} = useContext(AppContext);
    const blogId = location.pathname.split("/").at(-1); 

    async function fetchRelatedBlogs( ){
        setLoading(true);
        let url = `${newBaseUrl}get-blog?blogId=${blogId}`;
        console.log(url);

        try{
            const res = await fetch(url);
            const data = await res.json(); 
            setBlog(data.blog);
            setRelatedBlog(data.relatedBlogs);
        }       
        catch(error){
            console.log("Error aaya in  blog id wali call");
            setBlog(null);
            setRelatedBlog([]); 
        }
        setLoading(false);
    }
    useEffect( ()=>{
        if(blogId){
            fetchRelatedBlogs();
        }
    },[location.pathname] );

    return (
        <div className=" w-full flex flex-col justify-center items-center ">
            <Header />
            <div className=" w-full max-w-[680px] mt-24 ">
                <button  className="rounded-md border-4 px-3.5 py-1  mb-4"
                    onClick={()=> navigation(-1)}
                >
                    back 
                </button>
            </div>
            {
                loading ? 
                (<div>
                    <p>Loading</p>
                </div>) :
                blog ? 
                (
                    <div className=" w-full max-w-[680px] h-full flex flex-col gap-y-2">
                    {/* this place */}
                        <BlogDetails post={blog} />
                        <h2  className="rounded-md border-2 px-3.5 py-1  m-4 font-semibold w-36" >Related Blogs</h2>
                        {
                            relatedBlog.map ( (post) =>(
                                <div key ={post.id}>
                                    <BlogDetails post={post} />
                                </div>
                            ))
                        }
                    </div>
                ) : 
                (<div>
                    <p>No Blog Found</p>
                </div>) 
               
            }
            <Pagination/>
        </div>

    );
}

export default BlogPage;
