import "./App.css"

export default function App() {

  const {fetchBlogPosts} = useContext(AppContext);

  useEffect( ()=> {
    fetchBlogPosts();
  },[]);

  return (
   /* <div className="w-full h-full flex flex-col justify-center items-center gap-y-1 ">
