import React, {useContext} from "react";
import { AppContext } from "../context/AppContext";
const Blogs = () => {
    //consume
    const {loading, posts} = useContext(AppContext);

    return(
        <div>
            {
                loading ? (<Spinner />) : (
                    posts.length === 0 ? 
                    (<div><p>No Post Found</p></div>) : 
                    (posts.map )
                )
            }
        </div>
    )
}

export default Blogs;
