   const [page , setPage] = useState(1);
   const [totalPages , setTotalPages] = useState(null); 

   const value = {
     posts,
     setPosts,
     loading,
     setLoading,
     page,
     setPage,
     totalPages,
     setTotalPages,
   };
}
