import React from "react";
import { NavLink } from "react-router-dom";

const BlogDetails = ({post}) =>{


    return(
        <div className="flex  flex-col w-full left-[50px]">
            <NavLink to={`/blog/${post.id}`}>
                <span className="flex flex-col w-full justify-center items-center">{post.title}</span>
            </NavLink> 
            <p className="flex flex-col w-full justify-center items-center">
                By
                <span className="flex flex-col w-full justify-center items-center">{post.author}</span>
                on {" "}
                <NavLink to={`/categories/${post.category.replaceAll(" ","-" )}`}> 
                    <span className="flex flex-col w-full justify-center items-center">{post.category}</span>
                </NavLink>
            </p>
            <p className="flex flex-col w-full justify-center items-center">Posted on{post.date}</p>
            <p className="flex flex-col w-full justify-center items-center"> {post.content} </p>
            <div className="flex flex-col w-full justify-center items-center">
                {post.tags.map ( (tag,index) => (
                    <NavLink key={index} to={`/tags/${tag.replaceAll(" ","-")}`}>
                        <span className="flex flex-col w-full justify-center items-center">{`#${tag}`}</span>
                    </NavLink>
                ))}
            </div>
            
        </div>
    );
}

export default BlogDetails;
